import React, { useState } from 'react';

const Projects = () => {
  // Define an array of projects
  const [projects] = useState([
    {
      title: 'Portfolio Website',
      description: 'A personal portfolio website to showcase my work.',
      github: 'https://github.com/username/portfolio',
      date: '2023-12-15', // Use ISO format for easier sorting
    },
    {
      title: 'Task Manager App',
      description: 'A task management tool with drag-and-drop functionality.',
      github: 'https://github.com/username/task-manager',
      date: '2023-10-05',
    },
    // Add more projects as needed
  ]);

  // Sort projects by date (soonest at the top)
  const sortedProjects = [...projects].sort((a, b) => new Date(b.date) - new Date(a.date));

  return (
    <div>
      <h1>Projects</h1>
      <p>Here are my projects:</p>
      <div>
        {sortedProjects.map((project, index) => (
          <div key={index} style={styles.projectCard}>
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            <a href={project.github} target="_blank" rel="noopener noreferrer" style={styles.button}>
              View on GitHub
            </a>
            <p style={styles.date}>Date: {new Date(project.date).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  projectCard: {
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '16px',
    margin: '16px 0',
    backgroundColor: '#f9f9f9',
  },
  button: {
    display: 'inline-block',
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: '#fff',
    textDecoration: 'none',
    borderRadius: '4px',
    marginTop: '8px',
  },
  date: {
    color: '#555',
    fontSize: '0.9rem',
    marginTop: '8px',
  },
};

export default Projects;
